ll fpow(ll a,ll b)
{
	ll ans=1;
	 a%=mod;
	while(b>0){
		if(b&1) ans=(ans%mod*a%mod)%mod;
		a=(a%mod*a%mod)%mod;
		b>>=1;	
	}
	return ans%mod;
}
ll exgcd(ll a,ll b,ll &x,ll &y)
{
	if(!b){x=1;y=0;return a;}
	ll d=exgcd(b,a%b,x,y);	
	ll z=x;x=y;y=z-y*(a/b);	
	return d;
}
ll inv(ll a,ll mod)
{
	ll x,y;
	ll d=exgcd(a,mod,x,y);
	if(d==1) return (x%mod+mod)%mod;
	return -1;
	
}
